import React, { useEffect, useState } from 'react';
import maplibregl from 'maplibre-gl';
import 'maplibre-gl/dist/maplibre-gl.css';

const WarehouseDashboard = () => {
  const [activeTab, setActiveTab] = useState('incoming'); // Tab state: 'incoming' or 'outgoing'
  const [warehouseId, setWarehouseId] = useState(''); // User-specified warehouse ID
  const [trucks, setTrucks] = useState([]);
  const [error, setError] = useState(null);
  const [map, setMap] = useState(null); // MapLibre instance
  const [selectedTruck, setSelectedTruck] = useState(null); // Selected truck for route display

  useEffect(() => {
    if (!warehouseId) return;

    // Determine the API endpoint based on the active tab and warehouseId
    const endpoint =
      activeTab === 'incoming'
        ? `/api/getIncomingLiveTrucks?warehouseId=${warehouseId}`
        : `/api/getOutgoingTrucks?warehouseId=${warehouseId}`;

    // Fetch data from the API
    fetch(endpoint)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Error: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setTrucks(data.incomingTrucks || data.outgoing_trucks || []);
      })
      .catch((err) => {
        setError(err.message);
      });
  }, [activeTab, warehouseId]); // Re-fetch data when the active tab or warehouseId changes

  useEffect(() => {
    // Initialize the MapLibre map
    const mapInstance = new maplibregl.Map({
      container: 'map', // Container ID
      style: 'https://demotiles.maplibre.org/style.json', // Map style URL
      center: [78.9629, 20.5937], // Center of the map (India as an example)
      zoom: 4,
    });

    setMap(mapInstance);

    // Cleanup map instance on component unmount
    return () => mapInstance.remove();
  }, []);

  const handleTruckClick = (truck) => {
    setSelectedTruck(truck);
    // Simulate fetching geofence and displaying the route
    // Actual implementation omitted for brevity
  };

  return (
    <div style={{ display: 'flex' }}>
      <div style={{ flex: 1, padding: '20px' }}>
        <h1>Truck Dashboard</h1>

        {/* Warehouse ID Input */}
        <div style={{ marginBottom: '20px' }}>
          <label>
            <strong>Warehouse ID:</strong>{' '}
            <input
              type="text"
              value={warehouseId}
              onChange={(e) => setWarehouseId(e.target.value)}
              placeholder="Enter Warehouse ID"
              style={{ padding: '5px', marginRight: '10px' }}
            />
          </label>
          <button
            onClick={() => {
              if (!warehouseId) {
                setError('Please enter a valid warehouse ID.');
                return;
              }
              setError(null);
            }}
            style={{
              padding: '5px 10px',
              backgroundColor: '#007bff',
              color: '#fff',
              border: 'none',
              cursor: 'pointer',
            }}
          >
            Fetch Trucks
          </button>
        </div>

        {/* Tab Navigation */}
        <div style={{ marginBottom: '20px' }}>
          <button
            style={{
              padding: '10px 20px',
              marginRight: '10px',
              backgroundColor: activeTab === 'incoming' ? '#007bff' : '#ccc',
              color: '#fff',
              border: 'none',
              cursor: 'pointer',
            }}
            onClick={() => setActiveTab('incoming')}
          >
            Incoming Trucks
          </button>
          <button
            style={{
              padding: '10px 20px',
              backgroundColor: activeTab === 'outgoing' ? '#007bff' : '#ccc',
              color: '#fff',
              border: 'none',
              cursor: 'pointer',
            }}
            onClick={() => setActiveTab('outgoing')}
          >
            Outgoing Trucks
          </button>
        </div>

        {error ? (
          <p style={{ color: 'red' }}>Error: {error}</p>
        ) : (
          <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th>Truck ID</th>
                <th>Name</th>
                <th>Current Location</th>
                <th>Destination</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {trucks.length > 0 ? (
                trucks.map((truck) => (
                  <tr
                    key={truck.truck_id}
                    onClick={() => handleTruckClick(truck)} // Handle truck click
                    style={{ cursor: 'pointer' }}
                  >
                    <td>{truck.truck_id}</td>
                    <td>{truck.name}</td>
                    <td>{truck.current_location}</td>
                    <td>{truck.current_destination}</td>
                    <td>{truck.truck_status}</td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="5" style={{ textAlign: 'center' }}>
                    No {activeTab === 'incoming' ? 'incoming' : 'outgoing'} trucks found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        )}
      </div>

      {/* Map Container */}
      <div
        id="map"
        style={{
          flex: 2,
          height: '100vh',
          position: 'relative',
        }}
      >
        {/* Map Action Buttons */}
        <div
          style={{
            position: 'absolute',
            top: '10px',
            left: '10px',
            zIndex: 10,
            backgroundColor: '#fff',
            padding: '10px',
            borderRadius: '5px',
            boxShadow: '0 2px 5px rgba(0, 0, 0, 0.2)',
          }}
        >
          <button
            style={{
              display: 'block',
              marginBottom: '10px',
              padding: '10px 20px',
              backgroundColor: activeTab === 'incoming' ? '#007bff' : '#ccc',
              color: '#fff',
              border: 'none',
              cursor: 'pointer',
              width: '100%',
            }}
            onClick={() => setActiveTab('incoming')}
          >
            Incoming Trucks
          </button>
          <button
            style={{
              display: 'block',
              marginBottom: '10px',
              padding: '10px 20px',
              backgroundColor: activeTab === 'outgoing' ? '#007bff' : '#ccc',
              color: '#fff',
              border: 'none',
              cursor: 'pointer',
              width: '100%',
            }}
            onClick={() => setActiveTab('outgoing')}
          >
            Outgoing Trucks
          </button>
        </div>
      </div>
    </div>
  );
};

export default WarehouseDashboard;
