import React, { useState } from 'react';
import { Layout, Sidebar, MainContent } from '../components/ui/Layout';
import TraccarMap from './TraccarMap';
import AddTruck from '../components/AddTruck';
import AddUser from '../components/AddUser';
import AddWarehouse from '../components/AddWarehouse';
import AdminSchedulePage from '../components/AdminSchedulePage';
import ManageUsers from '../components/ManagePermissions';
import SetCredentials from '../components/ManageCredentials';
import {
  Map as MapIcon,
  Truck,
  UserPlus,
  Building2,
  Calendar,
  Menu,
  LogOut,
  Key,
  UserCheck,
} from 'lucide-react';

const AdminDashboard = () => {
  const [currentView, setCurrentView] = useState('map');
  const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);

  const menuItems = [
    { id: 'map', icon: <MapIcon className="w-5 h-5" />, label: 'Live Tracking' },
    { id: 'trucks', icon: <Truck className="w-5 h-5" />, label: 'Add Truck' },
    { id: 'users', icon: <UserPlus className="w-5 h-5" />, label: 'Add User' },
    { id: 'warehouse', icon: <Building2 className="w-5 h-5" />, label: 'Add Warehouse' },
    { id: 'schedule', icon: <Calendar className="w-5 h-5" />, label: 'Schedule' },
    { id: 'manage-users', icon: <UserCheck className="w-5 h-5" />, label: 'Manage Users' },
    { id: 'credentials', icon: <Key className="w-5 h-5" />, label: 'Set Credentials' },
  ];

  const renderContent = () => {
    switch (currentView) {
      case 'map':
        return <TraccarMap />;
      case 'trucks':
        return <AddTruck />;
      case 'users':
        return <AddUser />;
      case 'warehouse':
        return <AddWarehouse />;
      case 'schedule':
        return <AdminSchedulePage />;
      case 'manage-users':
        return <ManageUsers />;
      case 'credentials':
        return <SetCredentials />;
      default:
        return <TraccarMap />;
    }
  };

  const handleLogout = () => {
    console.log('Logging out...');
  };

  const handleNavigation = (direction) => {
    const currentIndex = menuItems.findIndex((item) => item.id === currentView);
    let newIndex = direction === 'next' ? currentIndex + 1 : currentIndex - 1;

    if (newIndex >= menuItems.length) newIndex = 0; // Wrap to start
    if (newIndex < 0) newIndex = menuItems.length - 1; // Wrap to end

    setCurrentView(menuItems[newIndex].id);
  };

  const resetMapView = () => {
    console.log('Resetting map view...');
    // You can add any specific logic here to reset the map
  };

  return (
    <Layout>
      {/* Sidebar */}
      <Sidebar
        collapsed={isSidebarCollapsed}
        className={`h-screen bg-gray-900 text-white transition-all duration-300 ${
          isSidebarCollapsed ? 'w-20' : 'w-64'
        }`}
      >
        <div className="flex flex-col h-full">
          {/* Header */}
          <div className="p-4 flex items-center justify-between border-b border-gray-800">
            {!isSidebarCollapsed && <h1 className="text-xl font-bold">Admin Panel</h1>}
            <button
              onClick={() => setIsSidebarCollapsed(!isSidebarCollapsed)}
              className="p-2 rounded-lg hover:bg-gray-800 transition-colors"
            >
              <Menu className="w-5 h-5" />
            </button>
          </div>

          {/* Navigation */}
          <nav className="flex-1 py-4">
            <ul className="space-y-2">
              {menuItems.map((item) => (
                <li key={item.id}>
                  <button
                    onClick={() => setCurrentView(item.id)}
                    className={`w-full p-3 flex items-center space-x-3 rounded-lg transition-colors ${
                      currentView === item.id
                        ? 'bg-blue-600 text-white'
                        : 'text-gray-300 hover:bg-gray-800'
                    }`}
                  >
                    {item.icon}
                    {!isSidebarCollapsed && <span>{item.label}</span>}
                  </button>
                </li>
              ))}
            </ul>
          </nav>

          {/* Footer */}
          <div className="p-4 border-t border-gray-800">
            <button
              onClick={handleLogout}
              className="w-full p-3 flex items-center space-x-3 text-gray-300 hover:bg-gray-800 rounded-lg transition-colors"
            >
              <LogOut className="w-5 h-5" />
              {!isSidebarCollapsed && <span>Logout</span>}
            </button>
          </div>
        </div>
      </Sidebar>

      {/* Main Content */}
      <MainContent className="flex-1 bg-gray-100 relative">
        <div className="h-full overflow-auto relative">
          {currentView === 'map' && (
            <div className="absolute top-4 right-4 flex space-x-4 z-10">
              <button
                onClick={() => handleNavigation('prev')}
                className="p-3 bg-gray-800 text-white rounded-lg shadow-lg hover:bg-gray-700"
              >
                Previous
              </button>
              <button
                onClick={resetMapView}
                className="p-3 bg-gray-800 text-white rounded-lg shadow-lg hover:bg-gray-700"
              >
                Reset View
              </button>
              <button
                onClick={() => handleNavigation('next')}
                className="p-3 bg-gray-800 text-white rounded-lg shadow-lg hover:bg-gray-700"
              >
                Next
              </button>
            </div>
          )}

          <div>{renderContent()}</div>
        </div>
      </MainContent>
    </Layout>
  );
};

export default AdminDashboard;
