import React, { useState, useEffect } from 'react';
import { Truck, AlertTriangle, CheckCircle, Activity, MapPin, Box, Navigation2 } from 'lucide-react';

const Reports = () => {
  const [trucks, setTrucks] = useState([]);
  const [reportData, setReportData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isFetching, setIsFetching] = useState(false);

  useEffect(() => {
    fetchTrucks();
    const intervalId = setInterval(() => {
      fetchTrucks();
    }, 15000);
    return () => clearInterval(intervalId);
  }, []);

  const fetchTrucks = async () => {
    if (isFetching) return;
    setIsFetching(true);
    setLoading(true);
    setError(null);

    try {
      const response = await fetch('http://localhost:8000/api/devices', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ key: 'value' }),
      });

      if (!response.ok) throw new Error('Failed to fetch trucks');

      const data = await response.json();
      setTrucks(data || []);
      await fetchTruckDetails(data || []);
    } catch (error) {
      console.error('Error fetching trucks:', error);
      setError(error.message);
    } finally {
      setIsFetching(false);
      setLoading(false);
    }
  };

  const fetchTruckDetails = async (trucks) => {
    try {
      const details = await Promise.all(
        trucks.map(async (truck) => {
          try {
            const [capacity, status, location] = await Promise.all([
              getTruckCapacity(truck.uniqueId),
              getTruckStatus(truck.uniqueId),
              getTruckLocation(truck.uniqueId),
            ]);

            const filledCapacity =
              capacity.incoming_trucks?.length > 0
                ? Math.round(capacity.incoming_trucks[0].filled_capacity * 100) / 100
                : null;

            return {
              truckId: truck.uniqueId,
              name: truck.name || `Truck ${truck.uniqueId}`,
              capacityUtilization: filledCapacity,
              currentStatus: status.status || 'Unknown',
              atWarehouse: status.at_warehouse || false,
              onRoute: status.on_route || false,
              currentLocation: location.current_location_name || 'Unknown',
              currentDestination: location.current_destination_name || 'Not Set',
              lastUpdated: status.lastUpdated || new Date().toISOString(),
            };
          } catch (err) {
            console.error(`Error fetching details for truck ${truck.uniqueId}:`, err);
            return null;
          }
        })
      );

      setReportData(details.filter((detail) => detail !== null));
    } catch (error) {
      console.error('Error fetching truck details:', error);
      setError('Failed to fetch truck details');
    }
  };

  const getTruckCapacity = async (uniqueId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/getTruckCapacity?truckId=${uniqueId}`);
      if (!response.ok) throw new Error('Failed to fetch truck capacity');
      return await response.json();
    } catch (error) {
      console.error(`Error fetching capacity for truck ${uniqueId}:`, error);
      return {};
    }
  };

  const getTruckStatus = async (uniqueId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/getTruckStatus?uniqueId=${uniqueId}`);
      if (!response.ok) throw new Error('Failed to fetch truck status');
      return await response.json();
    } catch (error) {
      console.error(`Error fetching status for truck ${uniqueId}:`, error);
      return {};
    }
  };

  const getTruckLocation = async (uniqueId) => {
    try {
      const response = await fetch('http://localhost:8000/api/getTruckLocation', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ truck_id: uniqueId }),
      });
      if (!response.ok) throw new Error('Failed to fetch truck location');
      return await response.json();
    } catch (error) {
      console.error(`Error fetching location for truck ${uniqueId}:`, error);
      return {};
    }
  };

  const getStatusColor = (status) => {
    switch (status?.toLowerCase()) {
      case 'active':
        return 'bg-green-900/50 text-green-200 border-green-500/30';
      case 'inactive':
        return 'bg-red-900/50 text-red-200 border-red-500/30';
      case 'maintenance':
        return 'bg-yellow-900/50 text-yellow-200 border-yellow-500/30';
      default:
        return 'bg-gray-900/50 text-gray-200 border-gray-500/30';
    }
  };

  const getCapacityColor = (capacity) => {
    if (capacity === null || capacity === undefined) return 'bg-gray-900/50';
    if (capacity >= 80) return 'bg-red-900/50';
    if (capacity >= 50) return 'bg-yellow-900/50';
    return 'bg-green-900/50';
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-6 animate-fade-in">
        <div className="bg-red-900/20 border border-red-900 text-red-500 px-4 py-3 rounded-lg flex items-center gap-2">
          <AlertTriangle className="h-4 w-4" />
          <p>{error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="mx-6 my-6 bg-gray-900 rounded-lg shadow-xl text-white transition-all duration-300 opacity-0 animate-fade-in">
      <div className="border-b border-gray-800 p-6">
        <h2 className="text-2xl font-bold flex items-center gap-2">
          <Truck className="h-6 w-6 text-blue-500" />
          Fleet Status Dashboard
        </h2>
      </div>
      
      <div className="p-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
          <div className="bg-gray-800 p-4 rounded-lg border border-gray-700 transition-transform duration-300 hover:scale-105">
            <div className="flex items-center gap-2 text-gray-400 mb-2">
              <Truck className="h-4 w-4" />
              <span>Total Trucks</span>
            </div>
            <div className="text-2xl font-bold">{reportData.length}</div>
          </div>
          
          <div className="bg-gray-800 p-4 rounded-lg border border-gray-700 transition-transform duration-300 hover:scale-105">
            <div className="flex items-center gap-2 text-gray-400 mb-2">
              <Activity className="h-4 w-4" />
              <span>Active Trucks</span>
            </div>
            <div className="text-2xl font-bold">
              {reportData.filter(truck => truck.currentStatus.toLowerCase() === 'active').length}
            </div>
          </div>
          
          <div className="bg-gray-800 p-4 rounded-lg border border-gray-700 transition-transform duration-300 hover:scale-105">
            <div className="flex items-center gap-2 text-gray-400 mb-2">
              <MapPin className="h-4 w-4" />
              <span>At Warehouse</span>
            </div>
            <div className="text-2xl font-bold">
              {reportData.filter(truck => truck.atWarehouse).length}
            </div>
          </div>
          
          <div className="bg-gray-800 p-4 rounded-lg border border-gray-700 transition-transform duration-300 hover:scale-105">
            <div className="flex items-center gap-2 text-gray-400 mb-2">
              <Navigation2 className="h-4 w-4" />
              <span>On Route</span>
            </div>
            <div className="text-2xl font-bold">
              {reportData.filter(truck => truck.onRoute).length}
            </div>
          </div>
        </div>

        {reportData.length > 0 ? (
          <div className="bg-gray-800 rounded-lg border border-gray-700 overflow-hidden">
            <table className="w-full">
              <thead>
                <tr className="text-left border-b border-gray-700">
                  <th className="px-6 py-3 text-gray-400 font-medium">Truck Info</th>
                  <th className="px-6 py-3 text-gray-400 font-medium">Status</th>
                  <th className="px-6 py-3 text-gray-400 font-medium">Capacity</th>
                  <th className="px-6 py-3 text-gray-400 font-medium">Location</th>
                  <th className="px-6 py-3 text-gray-400 font-medium">Route</th>
                </tr>
              </thead>
              <tbody>
                {reportData.map((report, index) => (
                  <tr 
                    key={report.truckId}
                    className="border-b border-gray-700 hover:bg-gray-700/50 transition-colors duration-200 opacity-0 animate-fade-slide-in"
                    style={{ animationDelay: `${index * 100}ms` }}
                  >
                    <td className="px-6 py-4 flex items-center gap-2">
                      <Truck className="h-4 w-4 text-blue-500" />
                      {report.name}
                    </td>
                    <td className="px-6 py-4">
                      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium border ${getStatusColor(report.currentStatus)}`}>
                        {report.currentStatus}
                      </span>
                    </td>
                    <td className="px-6 py-4">
                      <div className="flex items-center gap-2">
                        <div className="w-16 bg-gray-700 rounded-full h-2">
                          <div 
                            className={`h-2 rounded-full ${getCapacityColor(report.capacityUtilization)}`}
                            style={{ width: `${report.capacityUtilization || 0}%` }}
                          ></div>
                        </div>
                        <span>{report.capacityUtilization || 'N/A'}%</span>
                      </div>
                    </td>
                    <td className="px-6 py-4 flex items-center gap-2">
                      <MapPin className="h-4 w-4 text-gray-500" />
                      {report.currentLocation}
                    </td>
                    <td className="px-6 py-4 flex items-center gap-2">
                      <Navigation2 className="h-4 w-4 text-gray-500" />
                      {report.currentDestination}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <div className="text-center py-12 text-gray-400">
            <Truck className="h-16 w-16 mx-auto mb-4 opacity-50" />
            <p>No truck data available.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Reports;
