import React, { useState, useEffect } from 'react';
import { Truck, AlertTriangle, CheckCircle, Clock, MapPin, Navigation, LayoutGrid } from 'lucide-react';
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";

const Reports = () => {
  const [trucks, setTrucks] = useState([]);
  const [reportData, setReportData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isFetching, setIsFetching] = useState(false);
  const [lastUpdated, setLastUpdated] = useState(new Date());

  useEffect(() => {
    fetchTrucks();
    const intervalId = setInterval(() => {
      fetchTrucks();
    }, 15000);
    return () => clearInterval(intervalId);
  }, []);

  const fetchTrucks = async () => {
    if (isFetching) return;
    setIsFetching(true);
    setLoading(true);
    setError(null);

    try {
      const response = await fetch('http://localhost:8000/api/devices', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ key: 'value' }),
      });

      if (!response.ok) throw new Error('Failed to fetch trucks');

      const data = await response.json();
      setTrucks(data || []);
      await fetchTruckDetails(data || []);
      setLastUpdated(new Date());
    } catch (error) {
      console.error('Error fetching trucks:', error);
      setError(error.message);
    } finally {
      setIsFetching(false);
      setLoading(false);
    }
  };

  const fetchTruckDetails = async (trucks) => {
    try {
      const details = await Promise.all(
        trucks.map(async (truck) => {
          try {
            const [capacity, status, location] = await Promise.all([
              getTruckCapacity(truck.uniqueId),
              getTruckStatus(truck.uniqueId),
              getTruckLocation(truck.uniqueId),
            ]);

            const filledCapacity =
              capacity.incoming_trucks?.length > 0
                ? Math.round(capacity.incoming_trucks[0].filled_capacity * 100) / 100
                : null;

            return {
              truckId: truck.uniqueId,
              name: truck.name || `Truck ${truck.uniqueId}`,
              capacityUtilization: filledCapacity,
              currentStatus: status.status || 'Unknown',
              atWarehouse: status.at_warehouse || false,
              onRoute: status.on_route || false,
              currentLocation: location.current_location_name || 'Unknown',
              currentDestination: location.current_destination_name || 'Not Set',
              lastUpdated: status.lastUpdated || new Date().toISOString(),
            };
          } catch (err) {
            console.error(`Error fetching details for truck ${truck.uniqueId}:`, err);
            return null;
          }
        })
      );

      setReportData(details.filter((detail) => detail !== null));
    } catch (error) {
      console.error('Error fetching truck details:', error);
      setError('Failed to fetch truck details');
    }
  };

  const getTruckCapacity = async (uniqueId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/getTruckCapacity?truckId=${uniqueId}`);
      if (!response.ok) throw new Error('Failed to fetch truck capacity');
      return await response.json();
    } catch (error) {
      console.error(`Error fetching capacity for truck ${uniqueId}:`, error);
      return {};
    }
  };

  const getTruckStatus = async (uniqueId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/getTruckStatus?uniqueId=${uniqueId}`);
      if (!response.ok) throw new Error('Failed to fetch truck status');
      return await response.json();
    } catch (error) {
      console.error(`Error fetching status for truck ${uniqueId}:`, error);
      return {};
    }
  };

  const getTruckLocation = async (uniqueId) => {
    try {
      const response = await fetch('http://localhost:8000/api/getTruckLocation', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ truck_id: uniqueId }),
      });
      if (!response.ok) throw new Error('Failed to fetch truck location');
      return await response.json();
    } catch (error) {
      console.error(`Error fetching location for truck ${uniqueId}:`, error);
      return {};
    }
  };

  const getStatusBadgeColor = (status) => {
    switch (status.toLowerCase()) {
      case 'active':
        return 'bg-green-500/20 text-green-500 border-green-500';
      case 'inactive':
        return 'bg-gray-500/20 text-gray-400 border-gray-400';
      case 'maintenance':
        return 'bg-yellow-500/20 text-yellow-500 border-yellow-500';
      case 'error':
        return 'bg-red-500/20 text-red-500 border-red-500';
      default:
        return 'bg-blue-500/20 text-blue-500 border-blue-500';
    }
  };

  const getCapacityColor = (capacity) => {
    if (capacity === null) return 'bg-gray-500/20';
    if (capacity >= 90) return 'bg-red-500';
    if (capacity >= 75) return 'bg-yellow-500';
    return 'bg-green-500';
  };

  if (loading) {
    return (
      <div className="flex flex-col items-center justify-center h-96 gap-4">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
        <p className="text-gray-400 animate-pulse">Loading fleet status...</p>
      </div>
    );
  }

  if (error) {
    return (
      <Card className="mx-6 my-6 border-red-900/50">
        <CardContent className="pt-6">
          <div className="bg-red-900/20 border border-red-900/50 text-red-500 px-6 py-4 rounded-lg flex items-center gap-3">
            <AlertTriangle className="h-5 w-5" />
            <div>
              <p className="font-medium">Error Loading Fleet Status</p>
              <p className="text-sm text-red-400">{error}</p>
            </div>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="mx-6 my-6">
      <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-6">
        <div className="space-y-1">
          <CardTitle className="text-2xl font-bold flex items-center gap-2">
            <Truck className="h-6 w-6" />
            Fleet Status Dashboard
          </CardTitle>
          <p className="text-sm text-gray-400 flex items-center gap-1">
            <Clock className="h-4 w-4" />
            Last updated: {lastUpdated.toLocaleTimeString()}
          </p>
        </div>
        <button
          onClick={fetchTrucks}
          className="px-4 py-2 bg-blue-500/10 hover:bg-blue-500/20 text-blue-500 rounded-lg transition-colors flex items-center gap-2"
        >
          <LayoutGrid className="h-4 w-4" />
          Refresh
        </button>
      </CardHeader>
      <CardContent>
        {reportData.length > 0 ? (
          <div className="rounded-lg border overflow-hidden">
            <table className="w-full">
              <thead>
                <tr className="bg-gray-100/5">
                  <th className="px-6 py-3 text-left text-sm font-medium text-gray-400">Truck Info</th>
                  <th className="px-6 py-3 text-left text-sm font-medium text-gray-400">Status</th>
                  <th className="px-6 py-3 text-left text-sm font-medium text-gray-400">Capacity</th>
                  <th className="px-6 py-3 text-left text-sm font-medium text-gray-400">Location</th>
                  <th className="px-6 py-3 text-left text-sm font-medium text-gray-400">Next Destination</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-100/10">
                {reportData.map((report) => (
                  <tr key={report.truckId} className="hover:bg-gray-100/5 transition-colors">
                    <td className="px-6 py-4">
                      <div className="font-medium">{report.name}</div>
                      <div className="text-sm text-gray-400">ID: {report.truckId}</div>
                    </td>
                    <td className="px-6 py-4">
                      <span className={`px-3 py-1 rounded-full text-xs border ${getStatusBadgeColor(report.currentStatus)}`}>
                        {report.currentStatus}
                      </span>
                    </td>
                    <td className="px-6 py-4">
                      <div className="flex items-center gap-2">
                        <div className="w-24 h-2 rounded-full bg-gray-100/10">
                          <div
                            className={`h-2 rounded-full ${getCapacityColor(report.capacityUtilization)}`}
                            style={{ width: `${report.capacityUtilization || 0}%` }}
                          />
                        </div>
                        <span className="text-sm">{report.capacityUtilization || 'N/A'}%</span>
                      </div>
                    </td>
                    <td className="px-6 py-4">
                      <div className="flex items-center gap-2">
                        <MapPin className="h-4 w-4 text-gray-400" />
                        {report.currentLocation}
                      </div>
                    </td>
                    <td className="px-6 py-4">
                      <div className="flex items-center gap-2">
                        <Navigation className="h-4 w-4 text-gray-400" />
                        {report.currentDestination}
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <div className="text-center py-12 bg-gray-100/5 rounded-lg border border-dashed">
            <Truck className="h-12 w-12 mx-auto text-gray-400 mb-3" />
            <p className="text-gray-400">No truck data available</p>
            <button
              onClick={fetchTrucks}
              className="mt-4 px-4 py-2 bg-blue-500/10 hover:bg-blue-500/20 text-blue-500 rounded-lg transition-colors inline-flex items-center gap-2"
            >
              <LayoutGrid className="h-4 w-4" />
              Refresh Data
            </button>
          </div>
        )}
      </CardContent>
    </Card>
  );
};

export default Reports;
