import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix for default marker icons in Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

const AdminDashboard = () => {
  const [routeData, setRouteData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const warehouseData = [
    { 
      id: "W1", 
      name: "Central Hub",
      lat: 18.5204, 
      lng: 73.8554,            
      load: 15,
      unload: 13,
      processingTime: 30
    },
    { 
      id: "W2",
      name: "North Depot",
      lat: 18.5303, 
      lng: 73.9024,            
      load: 21,
      unload: 23,
      processingTime: 25
    },
    { 
      id: "W3",
      name: "South Warehouse",
      lat: 18.4947, 
      lng: 73.8372,
      load: 45,
      unload: 53,
      processingTime: 35
    }
  ];

  useEffect(() => {
    const fetchRoute = async () => {
      try {
        const response = await fetch('http://localhost:8000/api/dynamicRoute', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(warehouseData),
        });

        if (!response.ok) {
          throw new Error('Failed to fetch route data');
        }

        const data = await response.json();
        setRouteData(data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchRoute();
  }, []);

  if (loading) {
    return <div className="flex items-center justify-center h-full">Loading map data...</div>;
  }

  if (error) {
    return <div className="flex items-center justify-center h-full text-red-500">Error: {error}</div>;
  }

  // Center the map on the first warehouse
  const center = [warehouseData[0].lat, warehouseData[0].lng];

  // Create route lines from the coordinates
  const routeLines = routeData?.route_details?.map((segment, index) => {
    const coords = routeData.route_coordinates
      .filter(point => 
        (point.warehouse_id === segment.from_id || point.warehouse_id === segment.to_id))
      .map(point => point.coordinate);
    
    return (
      <Polyline
        key={`route-${index}`}
        positions={coords}
        color="blue"
        weight={3}
        opacity={0.7}
      />
    );
  });

  return (
    <div className="h-full w-full">
      <MapContainer
        center={center}
        zoom={13}
        style={{ height: '100%', width: '100%' }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />
        
        {/* Render warehouse markers */}
        {warehouseData.map((warehouse) => (
          <Marker
            key={warehouse.id}
            position={[warehouse.lat, warehouse.lng]}
          >
            <Popup>
              <div>
                <h3 className="font-bold">{warehouse.name}</h3>
                <p>Load: {warehouse.load}</p>
                <p>Unload: {warehouse.unload}</p>
                <p>Processing Time: {warehouse.processingTime} min</p>
              </div>
            </Popup>
          </Marker>
        ))}

        {/* Render route lines */}
        {routeLines}
      </MapContainer>
    </div>
  );
};

export default AdminDashboard;
