import React from 'react';
import { AlertCircle, XCircle, Info, CheckCircle2, AlertTriangle, X } from 'lucide-react';

const AlertDescription = ({ children, className = '' }) => (
  <div className={`text-sm ${className}`}>{children}</div>
);

const Alert = React.forwardRef(({ 
  children, 
  variant = 'default',
  title,
  onClose,
  className = '',
  ...props 
}, ref) => {
  const variants = {
    default: {
      containerClass: 'bg-gray-50 border-gray-200 text-gray-800',
      iconClass: 'text-gray-500'
    },
    destructive: {
      containerClass: 'bg-red-50 border-red-200 text-red-800',
      iconClass: 'text-red-500'
    },
    success: {
      containerClass: 'bg-green-50 border-green-200 text-green-800',
      iconClass: 'text-green-500'
    },
    warning: {
      containerClass: 'bg-yellow-50 border-yellow-200 text-yellow-800',
      iconClass: 'text-yellow-500'
    },
    info: {
      containerClass: 'bg-blue-50 border-blue-200 text-blue-800',
      iconClass: 'text-blue-500'
    }
  };

  const getIcon = () => {
    switch (variant) {
      case 'destructive':
        return XCircle;
      case 'success':
        return CheckCircle2;
      case 'warning':
        return AlertTriangle;
      case 'info':
        return Info;
      default:
        return AlertCircle;
    }
  };

  const Icon = getIcon();
  const { containerClass, iconClass } = variants[variant] || variants.default;

  return (
    <div
      ref={ref}
      role="alert"
      className={`relative flex items-start p-4 mb-4 border rounded-lg ${containerClass} ${className}`}
      {...props}
    >
      <div className={`flex-shrink-0 mr-3 ${iconClass}`}>
        <Icon className="h-5 w-5" />
      </div>

      <div className="flex-1 min-w-0">
        {title && (
          <h3 className="text-sm font-medium mb-1">
            {title}
          </h3>
        )}
        {children}
      </div>

      {onClose && (
        <button 
          onClick={onClose}
          className={`absolute top-4 right-4 ${iconClass} hover:opacity-75 transition-opacity`}
          aria-label="Close alert"
        >
          <X className="h-4 w-4" />
        </button>
      )}
    </div>
  );
});

Alert.displayName = 'Alert';

// Toast notification component
const Toast = ({ 
  message, 
  variant = 'default', 
  onClose,
  duration = 5000 
}) => {
  React.useEffect(() => {
    if (duration && onClose) {
      const timer = setTimeout(onClose, duration);
      return () => clearTimeout(timer);
    }
  }, [duration, onClose]);

  return (
    <div className="fixed top-4 right-4 z-50 animate-slide-in">
      <Alert 
        variant={variant} 
        onClose={onClose}
        className="shadow-lg min-w-[300px] max-w-[400px]"
      >
        <AlertDescription>{message}</AlertDescription>
      </Alert>
    </div>
  );
};

// Banner component
const Banner = ({ 
  message, 
  variant = 'default',
  onClose 
}) => (
  <div className="w-full animate-slide-down">
    <Alert 
      variant={variant} 
      onClose={onClose}
      className="rounded-none border-x-0"
    >
      <AlertDescription>{message}</AlertDescription>
    </Alert>
  </div>
);

export { Alert, AlertDescription, Toast, Banner };
