import React, { useState, useEffect } from 'react';

const AssignWarehouses = () => {
  const [trucks, setTrucks] = useState([]);
  const [warehouses, setWarehouses] = useState({ warehouse_ids: [], warehouse_names: [] });
  const [selectedTruck, setSelectedTruck] = useState('');
  const [selectedWarehouses, setSelectedWarehouses] = useState([]);
  const [message, setMessage] = useState({ type: '', text: '' });
  const [isLoading, setIsLoading] = useState(false);
  const [assignmentProgress, setAssignmentProgress] = useState(0);

  useEffect(() => {
    fetchTrucks();
    fetchWarehouses();
  }, []);

  const fetchTrucks = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/devices', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ key: 'value' })
      });
      
      if (!response.ok) throw new Error('Failed to fetch trucks');
      
      const data = await response.json();
      console.log('Trucks data:', data);
      setTrucks(data || []);
    } catch (error) {
      console.error('Error fetching trucks:', error);
      setMessage({ type: 'error', text: 'Failed to load trucks' });
    }
  };

  const fetchWarehouses = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/getAllWarehouseNames', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ key: 'value' })
      });
      
      if (!response.ok) throw new Error('Failed to fetch warehouses');
      
      const data = await response.json();
      console.log('Warehouses data:', data);
      setWarehouses(data);
    } catch (error) {
      console.error('Error fetching warehouses:', error);
      setMessage({ type: 'error', text: 'Failed to load warehouses' });
    }
  };

  const handleTruckChange = (e) => {
    setSelectedTruck(e.target.value);
  };

  const handleWarehouseSelect = (warehouseId, index) => {
    setSelectedWarehouses(prev => {
      if (prev.includes(warehouseId)) {
        return prev.filter(id => id !== warehouseId);
      } else {
        return [...prev, warehouseId];
      }
    });
  };

  const assignWarehouse = async (deviceId, geofenceId) => {
    try {
      const response = await fetch('http://localhost:8000/api/devices/assignWarehouses', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          deviceId: deviceId,
          geofenceId: geofenceId
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || `Failed to assign warehouse ${geofenceId}`);
      }

      return await response.json();
    } catch (error) {
      throw error;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setAssignmentProgress(0);
    let successCount = 0;
    let errors = [];
    
    try {
      // Find the selected truck's ID
      const selectedTruckObj = trucks.find(truck => truck.uniqueId === selectedTruck);
      if (!selectedTruckObj) {
        throw new Error('Selected truck not found');
      }

      // Process each warehouse assignment sequentially
      for (let i = 0; i < selectedWarehouses.length; i++) {
        try {
          await assignWarehouse(selectedTruckObj.id.toString(), selectedWarehouses[i]);
          successCount++;
        } catch (error) {
          errors.push(`Warehouse ${selectedWarehouses[i]}: ${error.message}`);
        }
        setAssignmentProgress(((i + 1) / selectedWarehouses.length) * 100);
      }

      // Set appropriate message based on results
      if (successCount === selectedWarehouses.length) {
        setMessage({ type: 'success', text: 'All warehouses assigned successfully!' });
      } else if (successCount > 0) {
        setMessage({ 
          type: 'warning', 
          text: `${successCount} out of ${selectedWarehouses.length} warehouses assigned successfully. Errors: ${errors.join(', ')}`
        });
      } else {
        setMessage({ type: 'error', text: `Failed to assign warehouses. Errors: ${errors.join(', ')}` });
      }

      // Reset selections on success
      if (successCount > 0) {
        setSelectedWarehouses([]);
        setSelectedTruck('');
      }
    } catch (error) {
      console.error('Error in assignment process:', error);
      setMessage({ type: 'error', text: error.message || 'Failed to process warehouse assignments' });
    } finally {
      setIsLoading(false);
      setAssignmentProgress(0);
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-6">Assign Warehouses to Trucks</h2>
      
      {message.text && (
        <div className={`p-4 mb-6 rounded-md ${
          message.type === 'success' ? 'bg-green-100 text-green-700' : 
          message.type === 'warning' ? 'bg-yellow-100 text-yellow-700' :
          'bg-red-100 text-red-700'
        }`}>
          {message.text}
        </div>
      )}
      
      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label className="block text-sm font-medium mb-2">
            Select Truck
            <select
              value={selectedTruck}
              onChange={handleTruckChange}
              required
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
            >
              <option value="">Choose a truck</option>
              {trucks.map((truck) => (
                <option key={truck.uniqueId} value={truck.uniqueId}>
                  {truck.name ? `${truck.name} (${truck.uniqueId})` : `Truck ${truck.uniqueId}`}
                </option>
              ))}
            </select>
          </label>
        </div>

        <div>
          <label className="block text-sm font-medium mb-2">
            Select Warehouses
          </label>
          <div className="grid grid-cols-2 gap-4 border border-gray-200 rounded-md p-4">
            {warehouses.warehouse_ids.map((id, index) => (
              <label key={`warehouse-${id}`} className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={selectedWarehouses.includes(id)}
                  onChange={() => handleWarehouseSelect(id, index)}
                  className="rounded border-gray-300 text-blue-600 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
                />
                <span>{warehouses.warehouse_names[index]}</span>
              </label>
            ))}
          </div>
        </div>

        {isLoading && assignmentProgress > 0 && (
          <div className="w-full bg-gray-200 rounded-full h-2.5 mb-4">
            <div 
              className="bg-blue-600 h-2.5 rounded-full transition-all duration-300" 
              style={{ width: `${assignmentProgress}%` }}
            ></div>
          </div>
        )}

        <button
          type="submit"
          disabled={isLoading || !selectedTruck || selectedWarehouses.length === 0}
          className="w-full bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          {isLoading ? 'Assigning Warehouses...' : 'Assign Warehouses'}
        </button>
      </form>
    </div>
  );
};

export default AssignWarehouses;
