import React, { useState, useEffect } from 'react';

const Reports = () => {
  const [trucks, setTrucks] = useState([]);
  const [selectedTruck, setSelectedTruck] = useState('');
  const [reportData, setReportData] = useState([]);

  // Fetch trucks on component mount
  useEffect(() => {
    fetchTrucks();
  }, []);

  const fetchTrucks = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/devices', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ key: 'value' }),
      });

      if (!response.ok) throw new Error('Failed to fetch trucks');

      const data = await response.json();
      setTrucks(data || []);
    } catch (error) {
      console.error('Error fetching trucks:', error);
    }
  };

  const fetchReportData = async (truckId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/truckReports?truckId=${truckId}`);
      if (!response.ok) throw new Error('Failed to fetch report data');
      const data = await response.json();
      setReportData(data || []);
    } catch (error) {
      console.error('Error fetching report data:', error);
      setReportData([]);
    }
  };

  const handleTruckChange = (e) => {
    const truckId = e.target.value;
    setSelectedTruck(truckId);

    if (truckId) {
      fetchReportData(truckId);
    } else {
      setReportData([]);
    }
  };

  return (
    <div className="p-6 bg-white rounded-lg shadow">
      <h2 className="text-2xl font-bold mb-4">Truck Status Reports</h2>

      {/* Truck Selection Dropdown */}
      <div className="mb-6">
        <label className="block text-sm font-medium mb-2">
          Select Truck
        </label>
        <select
          value={selectedTruck}
          onChange={handleTruckChange}
          className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
        >
          <option value="">Choose a truck</option>
          {trucks.map((truck) => (
            <option key={truck.uniqueId} value={truck.uniqueId}>
              {truck.name ? `${truck.name} (${truck.uniqueId})` : `Truck ${truck.uniqueId}`}
            </option>
          ))}
        </select>
      </div>

      {/* Report Table */}
      {reportData.length > 0 ? (
        <table className="w-full border-collapse border border-gray-300">
          <thead>
            <tr>
              <th className="border border-gray-300 px-4 py-2">Truck ID</th>
              <th className="border border-gray-300 px-4 py-2">Current Location</th>
              <th className="border border-gray-300 px-4 py-2">Capacity Utilization</th>
              <th className="border border-gray-300 px-4 py-2">Last Updated</th>
            </tr>
          </thead>
          <tbody>
            {reportData.map((report, index) => (
              <tr key={index}>
                <td className="border border-gray-300 px-4 py-2">{report.truckId}</td>
                <td className="border border-gray-300 px-4 py-2">{report.currentLocation}</td>
                <td className="border border-gray-300 px-4 py-2">{report.capacityUtilization}</td>
                <td className="border border-gray-300 px-4 py-2">{report.lastUpdated}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : selectedTruck ? (
        <p className="text-gray-500">No report data available for the selected truck.</p>
      ) : (
        <p className="text-gray-500">Please select a truck to view its status report.</p>
      )}
    </div>
  );
};

export default Reports;
