// components/ui/dialog.js

import React, { useEffect } from 'react';
import ReactDOM from 'react-dom';
import { X } from 'lucide-react';
import PropTypes from 'prop-types';

/**
 * Dialog Component
 * Renders children inside a modal dialog when open is true.
 */
const Dialog = ({ open, onOpenChange, children }) => {
  // Close dialog on pressing Escape key
  useEffect(() => {
    const handleEsc = (event) => {
      if (event.key === 'Escape') {
        onOpenChange(false);
      }
    };
    if (open) {
      document.addEventListener('keydown', handleEsc);
    }
    return () => {
      document.removeEventListener('keydown', handleEsc);
    };
  }, [open, onOpenChange]);

  if (!open) return null;

  return ReactDOM.createPortal(
    <div
      className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50"
      onClick={() => onOpenChange(false)} // Close when clicking on the overlay
    >
      <div
        className="bg-white rounded-lg shadow-lg w-full max-w-lg p-6 relative"
        onClick={(e) => e.stopPropagation()} // Prevent closing when clicking inside the dialog
      >
        {children}
      </div>
    </div>,
    document.body
  );
};

Dialog.propTypes = {
  open: PropTypes.bool.isRequired,
  onOpenChange: PropTypes.func.isRequired,
  children: PropTypes.node.isRequired,
};

/**
 * DialogContent Component
 * Wraps the main content of the dialog.
 */
const DialogContent = ({ children, className = '' }) => (
  <div className={`space-y-4 ${className}`}>{children}</div>
);

DialogContent.propTypes = {
  children: PropTypes.node.isRequired,
  className: PropTypes.string,
};

/**
 * DialogHeader Component
 * Structures the header section of the dialog, typically containing the title and close button.
 */
const DialogHeader = ({ children, className = '' }) => (
  <div className={`flex justify-between items-center ${className}`}>
    {children}
  </div>
);

DialogHeader.propTypes = {
  children: PropTypes.node.isRequired,
  className: PropTypes.string,
};

/**
 * DialogTitle Component
 * Displays the title of the dialog.
 */
const DialogTitle = ({ children }) => (
  <h2 className="text-xl font-semibold">{children}</h2>
);

DialogTitle.propTypes = {
  children: PropTypes.node.isRequired,
};

/**
 * DialogClose Component
 * Provides a button to close the dialog.
 */
const DialogClose = ({ onClick }) => (
  <button
    onClick={onClick}
    className="text-gray-500 hover:text-gray-700 focus:outline-none"
    aria-label="Close dialog"
  >
    <X size={20} />
  </button>
);

DialogClose.propTypes = {
  onClick: PropTypes.func.isRequired,
};

export {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogClose,
};
