import React, { useState, useEffect } from 'react';
import { Truck, AlertTriangle, CheckCircle } from 'lucide-react';

const Reports = () => {
  const [trucks, setTrucks] = useState([]);
  const [reportData, setReportData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
  // Function to fetch data periodically
  const intervalId = setInterval(() => {
    fetchTrucks();
  }, 5000); // 5000ms = 5 seconds, adjust this as needed

  // Cleanup function to clear the interval when the component unmounts
  return () => clearInterval(intervalId);
}, []);


  const [isFetching, setIsFetching] = useState(false);

const fetchTrucks = async () => {
  if (isFetching) return; // Skip if a fetch is already in progress

  setIsFetching(true);
  try {
    const response = await fetch('http://localhost:8000/api/devices', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ key: 'value' }),
    });

    if (!response.ok) throw new Error('Failed to fetch trucks');

    const data = await response.json();
    setTrucks(data || []);
    await fetchTruckDetails(data || []);
  } catch (error) {
    setError(error.message);
    console.error('Error fetching trucks:', error);
  } finally {
    setIsFetching(false);
  }
};


  const fetchTruckDetails = async (trucks) => {
    try {
      const details = await Promise.all(
        trucks.map(async (truck) => {
          const [capacity, status, location] = await Promise.all([
            getTruckCapacity(truck.uniqueId),
            getTruckStatus(truck.uniqueId),
            getTruckLocation(truck.uniqueId),
          ]);

          const filledCapacity =
            capacity.incoming_trucks?.length > 0
              ? Math.round(capacity.incoming_trucks[0].filled_capacity * 100) / 100
              : null;

          return {
            truckId: truck.uniqueId,
            name: truck.name || `Truck ${truck.uniqueId}`,
            capacityUtilization: filledCapacity,
            currentStatus: status.status || 'Unknown',
            atWarehouse: status.at_warehouse || false,
            onRoute: status.on_route || false,
            currentLocation: location.current_location_name || 'Unknown',
            currentDestination: location.current_destination_name || 'Not Set',
            lastUpdated: status.lastUpdated || new Date().toISOString(),
          };
        })
      );

      setReportData(details);
    } catch (error) {
      console.error('Error fetching truck details:', error);
      setError('Failed to fetch truck details');
    }
  };

  const getTruckCapacity = async (uniqueId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/getTruckCapacity?truckId=${uniqueId}`);
      if (!response.ok) throw new Error('Failed to fetch truck capacity');
      return await response.json();
    } catch (error) {
      console.error(`Error fetching capacity for truck ${uniqueId}:`, error);
      return {};
    }
  };

  const getTruckStatus = async (uniqueId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/getTruckStatus?uniqueId=${uniqueId}`);
      if (!response.ok) throw new Error('Failed to fetch truck status');
      return await response.json();
    } catch (error) {
      console.error(`Error fetching status for truck ${uniqueId}:`, error);
      return {};
    }
  };

  const getTruckLocation = async (uniqueId) => {
    try {
      const response = await fetch('http://localhost:8000/api/getTruckLocation', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ truck_id: uniqueId }),
      });
      if (!response.ok) throw new Error('Failed to fetch truck location');
      return await response.json();
    } catch (error) {
      console.error(`Error fetching location for truck ${uniqueId}:`, error);
      return {};
    }
  };

  const getStatusColor = (status) => {
    switch (status.toLowerCase()) {
      case 'active':
        return 'text-green-500';
      case 'inactive':
        return 'text-red-500';
      case 'maintenance':
        return 'text-yellow-500';
      default:
        return 'text-gray-500';
    }
  };

  const getCapacityColor = (capacity) => {
    if (capacity === null) return 'bg-gray-200';
    if (capacity >= 90) return 'bg-red-500';
    if (capacity >= 75) return 'bg-yellow-500';
    return 'bg-green-500';
  };

  if (error) {
    return (
      <div className="p-6">
        <div className="bg-red-900/20 border border-red-900 text-red-500 px-4 py-3 rounded-lg flex items-center gap-2">
          <AlertTriangle className="h-4 w-4" />
          <p>{error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="mx-6 my-6 bg-gray-900 rounded-lg shadow-xl text-white">
      <div className="border-b border-gray-800 p-6">
        <div className="flex items-center justify-between">
          <h2 className="text-2xl font-bold flex items-center gap-2">
            <Truck className="h-6 w-6" />
            Fleet Status Dashboard
          </h2>
          <button 
            onClick={fetchTrucks} 
            className="px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded-lg transition-colors"
          >
            Refresh Data
          </button>
        </div>
      </div>
      
      <div className="p-6">
        {loading ? (
          <div className="flex justify-center items-center h-64">
            <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
          </div>
        ) : reportData.length > 0 ? (
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="text-left border-b border-gray-800">
                  <th className="px-6 py-3 text-gray-400 font-medium">Truck Info</th>
                  <th className="px-6 py-3 text-gray-400 font-medium">Status</th>
                  <th className="px-6 py-3 text-gray-400 font-medium">Capacity</th>
                  <th className="px-6 py-3 text-gray-400 font-medium">Location</th>
                  <th className="px-6 py-3 text-gray-400 font-medium">Route</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-800">
                {reportData.map((report) => (
                  <tr key={report.truckId} className="hover:bg-gray-800 transition-colors">
                    <td className="px-6 py-4">
                      <div className="flex flex-col">
                        <span className="font-medium">{report.name}</span>
                        <span className="text-sm text-gray-400">ID: {report.truckId}</span>
                      </div>
                    </td>
                    <td className="px-6 py-4">
                      <div className="flex items-center gap-2">
                        <span className={`h-2 w-2 rounded-full ${getStatusColor(report.currentStatus)}`}></span>
                        <span>{report.currentStatus}</span>
                      </div>
                    </td>
                    <td className="px-6 py-4">
                      <div className="flex items-center gap-2">
                        <div className="w-24 bg-gray-700 rounded-full h-2">
                          <div
                            className={`h-full rounded-full ${getCapacityColor(report.capacityUtilization)}`}
                            style={{ width: `${report.capacityUtilization || 0}%` }}
                          />
                        </div>
                        <span>{report.capacityUtilization || 'N/A'}%</span>
                      </div>
                    </td>
                    <td className="px-6 py-4">
                      <div className="flex flex-col">
                        <span>{report.currentLocation}</span>
                        <span className="text-sm text-gray-400">
                          {report.atWarehouse ? 'At Warehouse' : 'In Transit'}
                        </span>
                      </div>
                    </td>
                    <td className="px-6 py-4">
                      <div className="flex flex-col">
                        <span>{report.currentDestination}</span>
                        <span className="text-sm text-gray-400">
                          {report.onRoute ? 'On Route' : 'Not Started'}
                        </span>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <div className="text-center py-12">
            <CheckCircle className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <p className="text-gray-400">No trucks to display</p>
          </div>
        )}
      </div>
    </div>
  );
