import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Alert, AlertDescription } from "@/components/ui/alert";

const AddTruck = () => {
  const [formData, setFormData] = useState({
    name: '',
    uniqueId: '',
    max_volume: '',
    max_weight: '',
    mms_allocated_id: '',
    driver_id: '',
  });
  
  const [drivers, setDrivers] = useState([]);
  const [mmsList, setMmsList] = useState([]);
  const [message, setMessage] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    fetchDrivers();
    fetchMMS();
  }, []);

  const fetchDrivers = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/getAllDrivers', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ key: 'value' }),
      });
      
      if (!response.ok) throw new Error('Failed to fetch drivers');
      
      const data = await response.json();
      setDrivers(data.drivers || []);
    } catch (error) {
      console.error('Error fetching drivers:', error);
      setMessage({ type: 'error', text: 'Failed to load drivers' });
    }
  };

  const fetchMMS = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/getAllMMS', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ key: 'value' }),
      });
      
      if (!response.ok) throw new Error('Failed to fetch MMS');
      
      const data = await response.json();
      setMmsList(data.mms || []);
    } catch (error) {
      console.error('Error fetching MMS:', error);
      setMessage({ type: 'error', text: 'Failed to load MMS data' });
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSelectChange = (value, name) => {
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    
    try {
      const response = await fetch('http://localhost:8000/api/devices/new', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name: formData.name,
          uniqueId: formData.uniqueId,
          max_volume: parseInt(formData.max_volume),
          max_weight: parseInt(formData.max_weight),
          mms_allocated_id: parseInt(formData.mms_allocated_id),
          driver_id: parseInt(formData.driver_id),
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to add truck');
      }

      const data = await response.json();
      setMessage({ type: 'success', text: `Truck ${data.name} added successfully!` });
      
      // Reset form
      setFormData({
        name: '',
        uniqueId: '',
        max_volume: '',
        max_weight: '',
        mms_allocated_id: '',
        driver_id: '',
      });
    } catch (error) {
      console.error('Error adding truck:', error);
      setMessage({ type: 'error', text: error.message || 'Failed to add truck. Please try again.' });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Card className="w-full max-w-2xl mx-auto">
      <CardHeader>
        <CardTitle className="text-2xl">Add New Truck</CardTitle>
      </CardHeader>
      <CardContent>
        {message && (
          <Alert className={`mb-6 ${message.type === 'success' ? 'bg-green-50' : 'bg-red-50'}`}>
            <AlertDescription>
              {message.text}
            </AlertDescription>
          </Alert>
        )}
        
        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="space-y-2">
            <label className="text-sm font-medium">Truck Name</label>
            <Input
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              required
              placeholder="Enter truck name"
            />
          </div>

          <div className="space-y-2">
            <label className="text-sm font-medium">Unique ID</label>
            <Input
              name="uniqueId"
              value={formData.uniqueId}
              onChange={handleInputChange}
              required
              placeholder="Enter unique ID"
            />
          </div>

          <div className="space-y-2">
            <label className="text-sm font-medium">Max Volume</label>
            <Input
              type="number"
              name="max_volume"
              value={formData.max_volume}
              onChange={handleInputChange}
              required
              placeholder="Enter max volume"
            />
          </div>

          <div className="space-y-2">
            <label className="text-sm font-medium">Max Weight</label>
            <Input
              type="number"
              name="max_weight"
              value={formData.max_weight}
              onChange={handleInputChange}
              required
              placeholder="Enter max weight"
            />
          </div>

          <div className="space-y-2">
            <label className="text-sm font-medium">MMS</label>
            <Select 
              value={formData.mms_allocated_id} 
              onValueChange={(value) => handleSelectChange(value, 'mms_allocated_id')}
            >
              <SelectTrigger>
                <SelectValue placeholder="Select MMS" />
              </SelectTrigger>
              <SelectContent>
                {mmsList.map((mms) => (
                  <SelectItem key={mms.id} value={mms.id.toString()}>
                    {mms.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div className="space-y-2">
            <label className="text-sm font-medium">Driver</label>
            <Select 
              value={formData.driver_id} 
              onValueChange={(value) => handleSelectChange(value, 'driver_id')}
            >
              <SelectTrigger>
                <SelectValue placeholder="Select Driver" />
              </SelectTrigger>
              <SelectContent>
                {drivers.map((driver) => (
                  <SelectItem key={driver.id} value={driver.id.toString()}>
                    {driver.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <Button 
            type="submit" 
            className="w-full"
            disabled={isLoading}
          >
            {isLoading ? 'Adding Truck...' : 'Add Truck'}
          </Button>
        </form>
      </CardContent>
    </Card>
  );
};

export default AddTruck;
