import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix for default marker icons in Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

const DynamicRouteMap = () => {
  const [routeData, setRouteData] = useState(null);
  const [warehouseData, setWarehouseData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [map, setMap] = useState(null);
  const [selectedTruck, setSelectedTruck] = useState('');
  const [trucks, setTrucks] = useState([]);

  // Define route colors for different segments
  const routeColors = [
    '#FF5733', // Orange-red
    '#33FF57', // Bright green
    '#3357FF', // Blue
    '#FF33F6', // Pink
    '#33FFF6'  // Cyan
  ];

  // Fetch trucks on component mount
  useEffect(() => {
    const fetchTrucks = async () => {
      try {
        const response = await fetch('http://localhost:8000/api/devices', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ key: 'value' })
        });
        
        if (!response.ok) throw new Error('Failed to fetch trucks');
        
        const data = await response.json();
        setTrucks(data || []);
        if (data && data.length > 0) {
          setSelectedTruck(data[0].uniqueId); // Select first truck by default
        }
      } catch (error) {
        console.error('Error fetching trucks:', error);
        setError('Failed to load trucks');
      }
    };

    fetchTrucks();
  }, []);

  // Fetch warehouse data when truck is selected
  useEffect(() => {
    const fetchWarehouseData = async () => {
      if (!selectedTruck) return;
      
      try {
        setLoading(true);
        const response = await fetch(`http://localhost:8000/api/devices/geofences?uniqueId=${selectedTruck}`);
        
        if (!response.ok) {
          throw new Error('Failed to fetch warehouse data');
        }

        const data = await response.json();
        setWarehouseData(data);
        
        // Now fetch the route data with the new warehouse data
        await fetchRouteData(data);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchWarehouseData();
  }, [selectedTruck]);

  // Separate function to fetch route data
  const fetchRouteData = async (warehouses) => {
    try {
      const response = await fetch('http://localhost:8000/api/dynamicRoute', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(warehouses),
      });

      if (!response.ok) {
        throw new Error('Failed to fetch route data');
      }

      const data = await response.json();
      setRouteData(data);
      setLoading(false);
    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  };

  const handleTruckChange = (e) => {
    setSelectedTruck(e.target.value);
  };

  if (loading) {
    return <div className="flex items-center justify-center h-full">Loading map data...</div>;
  }

  if (error) {
    return <div className="flex items-center justify-center h-full text-red-500">Error: {error}</div>;
  }

  // Center the map on the first warehouse if available
  const center = warehouseData.length > 0 
    ? [warehouseData[0].lat, warehouseData[0].lng]
    : [18.5204, 73.8554]; // Default center

  // Create route lines from the coordinates with different colors
  const routeLines = routeData?.route_details?.map((segment, index) => {
    const segmentCoords = routeData.route_coordinates
      .filter(point => 
        (point.warehouse_id === segment.from_id || point.warehouse_id === segment.to_id))
      .map(point => point.coordinate);
    
    return (
      <Polyline
        key={`route-${index}`}
        positions={segmentCoords}
        color={routeColors[index % routeColors.length]}
        weight={4}
        opacity={0.8}
      />
    );
  });

  // Custom marker icon
  const warehouseIcon = new L.Icon({
    iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
    shadowSize: [41, 41]
  });

  return (
    <div className="h-screen w-full relative">
      {/* Truck selector dropdown */}
      <div className="absolute top-4 left-4 z-[1000] bg-white p-4 rounded-lg shadow-lg min-w-[200px]">
        <label className="block text-sm font-medium mb-2">
          Select Truck
          <select
            value={selectedTruck}
            onChange={handleTruckChange}
            className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Choose a truck</option>
            {trucks.map((truck) => (
              <option key={truck.uniqueId} value={truck.uniqueId}>
                {truck.name ? `${truck.name} (${truck.uniqueId})` : `Truck ${truck.uniqueId}`}
              </option>
            ))}
          </select>
        </label>
      </div>

      <MapContainer
        center={center}
        zoom={12}
        className="h-full w-full"
        whenCreated={setMap}
      >
        <TileLayer
          url="https://{s}.basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors &copy; <a href="https://carto.com/attributions">CARTO</a>'
          maxZoom={19}
        />
        
        {/* Render warehouse markers */}
        {warehouseData.map((warehouse) => (
          <Marker
            key={warehouse.id}
            position={[warehouse.lat, warehouse.lng]}
            icon={warehouseIcon}
          >
            <Popup>
              <div className="p-2">
                <h3 className="font-bold text-lg mb-2">{warehouse.name}</h3>
                <p className="text-sm">Load: {warehouse.load} units</p>
                <p className="text-sm">Unload: {warehouse.unload} units</p>
                <p className="text-sm">Processing: {warehouse.processingTime} min</p>
              </div>
            </Popup>
          </Marker>
        ))}

        {/* Render route lines */}
        {routeLines}
      </MapContainer>

      {/* Legend */}
      <div className="absolute bottom-4 right-4 bg-white p-4 rounded-lg shadow-lg z-[1000]">
        <h4 className="font-bold mb-2">Route Legend</h4>
        {routeData?.route_details?.map((segment, index) => (
          <div key={`legend-${index}`} className="flex items-center mb-1">
            <div 
              className="w-4 h-4 mr-2" 
              style={{ backgroundColor: routeColors[index % routeColors.length] }}
            ></div>
            <span className="text-sm">
              {segment.from_name} â†’ {segment.to_name}
            </span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DynamicRouteMap;
