import React, { useState, useEffect, useContext } from 'react';
import { 
  Calendar, Clock, MapPin, ArrowUp,Truck,Globe, ArrowUpRight, Search, Bell, Menu, X, 
  Package, Database, Settings, Users, BarChart, Box, FileText,ShieldCheck,Mail,
  PlusCircle, Edit, Trash2, CheckCircle, Truck as TruckIcon, 
  XCircle, Info, Warehouse, UserCheck 
} from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import { UserContext } from '../components/ui/UserContext';
// import { Alert, AlertDescription } from '../components/alert';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '../components/ui/dialog';
// import TraccarMap from '../components/TraccarMap.jsx';

const HomePage = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isLoginModalOpen, setIsLoginModalOpen] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loginError, setLoginError] = useState(null);

  const { setUserRole, setWarehouseAccess } = useContext(UserContext);
  const navigate = useNavigate();

  const features = [
    { icon: Truck, title: 'Express Delivery', description: 'Lightning-fast delivery to your doorstep' },
    { icon: Globe, title: 'Global Reach', description: 'Seamless international shipping solutions' },
    { icon: Package, title: 'Smart Tracking', description: 'Real-time package tracking and updates' },
    { icon: ShieldCheck, title: 'Secure Shipping', description: 'Your packages are in safe hands' },
    { icon: Clock, title: '24/7 Support', description: 'Round-the-clock customer assistance' },
    { icon: Mail, title: 'Mail Services', description: 'Comprehensive postal solutions' }
  ];

  useEffect(() => {
    const handleScroll = () => setIsScrolled(window.scrollY > 50);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleNavClick = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
    setIsMobileMenuOpen(false);
  };

  const handleLoginSubmit = (e) => {
    e.preventDefault();
    setLoginError(null);

    fetch('http://localhost:8000/api/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password }),
    })
      .then((res) => res.json().then((data) => ({ status: res.status, data })))
      .then(({ status, data }) => {
        if (status === 200 && data.success) {
          setUserRole(data.role);
          setWarehouseAccess(data.warehouse_access);

          if (data.role === 'admin') {
            navigate('/admin-dashboard');
          } else if (data.role === 'warehouse') {
            navigate('/warehouse-dashboard');
          } else if (data.role === 'scanner') {
            navigate('/scanner-dashboard');
          } else {
            navigate('/');
          }
        } else {
          setLoginError(data.error || 'Invalid Credentials');
        }
      })
      .catch(() => {
        setLoginError('Error connecting to server');
      });
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className={`fixed w-full z-50 transition-all duration-500 ${
        isScrolled ? 'bg-indigo-900/95 backdrop-blur-md shadow-lg' : 'bg-transparent'
      }`}>
        <div className="container mx-auto px-4 py-4">
          <div className="flex justify-between items-center">
            <div className="flex items-center space-x-2">
              <div className="w-10 h-10 bg-gradient-to-br from-cyan-400 to-purple-600 rounded-lg flex items-center justify-center">
                <span className="text-white font-bold text-xl">P+</span>
              </div>
              <h2 className="text-2xl font-bold text-white">PostPlus</h2>
            </div>
            
            <nav className="hidden md:flex items-center space-x-8">
              {['home', 'services', 'about'].map((section) => (
                <button
                  key={section}
                  onClick={() => handleNavClick(section)}
                  className="text-white opacity-90 hover:opacity-100 hover:text-cyan-400 transition-all duration-300"
                >
                  {section.charAt(0).toUpperCase() + section.slice(1)}
                </button>
              ))}
              <button
                onClick={() => setIsLoginModalOpen(true)}
                className="px-6 py-2 bg-gradient-to-r from-cyan-400 to-purple-500 text-white rounded-full hover:shadow-lg hover:scale-105 transition-all duration-300"
              >
                Login
              </button>
            </nav>

            <button
              className="md:hidden text-white"
              onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
            >
              {isMobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          </div>

          <div className={`md:hidden transition-all duration-300 ${
            isMobileMenuOpen ? 'max-h-64 opacity-100 mt-4' : 'max-h-0 opacity-0'
          } overflow-hidden`}>
            <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-4 space-y-2">
              {['home', 'services', 'about'].map((section) => (
                <button
                  key={section}
                  onClick={() => handleNavClick(section)}
                  className="block w-full text-left px-4 py-2 text-white rounded-lg hover:bg-white/10 transition-colors duration-300"
                >
                  {section.charAt(0).toUpperCase() + section.slice(1)}
                </button>
              ))}
              <button
                onClick={() => setIsLoginModalOpen(true)}
                className="block w-full text-left px-4 py-2 text-cyan-400 rounded-lg hover:bg-white/10 transition-colors duration-300"
              >
                Login
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Login Modal */}
      {isLoginModalOpen && (
        <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center">
          <div className="bg-white rounded-2xl p-8 max-w-md w-full mx-4 transform transition-all duration-300 scale-in-center shadow-2xl">
            <div className="flex justify-between items-center mb-8">
              <div>
                <h3 className="text-2xl font-bold text-gray-800">Welcome Back</h3>
                <p className="text-gray-500">Sign in to your account</p>
              </div>
              <button
                onClick={() => setIsLoginModalOpen(false)}
                className="p-2 hover:bg-gray-100 rounded-full transition-colors"
              >
                <X size={20} />
              </button>
            </div>
            <form onSubmit={handleLoginSubmit} className="space-y-6">
              <div>
                <input
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder="Username"
                  className="w-full px-4 py-3 rounded-xl border bg-gray-50 focus:outline-none focus:border-indigo-500 focus:bg-white transition-all duration-300"
                />
              </div>
              <div>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Password"
                  className="w-full px-4 py-3 rounded-xl border bg-gray-50 focus:outline-none focus:border-indigo-500 focus:bg-white transition-all duration-300"
                />
              </div>
              {loginError && (
                <div className="p-4 bg-red-50 border border-red-100 rounded-xl">
                  <p className="text-red-500 text-sm">{loginError}</p>
                </div>
              )}
              <button
                type="submit"
                className="w-full py-3 bg-gradient-to-r from-indigo-600 to-purple-600 text-white rounded-xl hover:shadow-lg hover:from-indigo-500 hover:to-purple-500 transition-all duration-300"
              >
                Sign in
              </button>
            </form>
          </div>
        </div>
      )}

      {/* Main Content */}
      <main>
        {/* Hero Section */}
        <section id="home" className="min-h-screen relative flex items-center justify-center overflow-hidden">
          <div className="absolute inset-0 bg-gradient-to-br from-indigo-900 via-purple-900 to-cyan-900">
            <div className="absolute inset-0 opacity-30 bg-[radial-gradient(circle_at_50%_50%,_rgba(255,255,255,0.1)_0%,_rgba(0,0,0,0)_50%)]"></div>
          </div>
          <div className="relative z-10 container mx-auto px-4 py-32 text-center">
            <h1 className="text-5xl md:text-7xl font-bold text-white mb-8 animate-fade-in">
              Next-Gen Delivery
              <span className="bg-gradient-to-r from-cyan-400 to-purple-400 text-transparent bg-clip-text"> Solutions</span>
            </h1>
            <p className="text-xl md:text-2xl text-white/90 mb-12 max-w-2xl mx-auto animate-fade-in-delay">
              Experience the future of package delivery with PostPlus's innovative tracking and delivery system
            </p>
            <div className="flex flex-col sm:flex-row justify-center items-center space-y-4 sm:space-y-0 sm:space-x-6">
              <button className="px-8 py-4 bg-white text-indigo-900 rounded-full font-bold hover:bg-cyan-400 hover:text-white transition-all duration-300 transform hover:scale-105 shadow-lg">
                Get Started
              </button>
              <button className="px-8 py-4 bg-white/10 text-white rounded-full font-bold hover:bg-white/20 transition-all duration-300">
                Learn More
              </button>
            </div>
          </div>
        </section>

        {/* Features Section */}
        <section id="services" className="py-24 bg-white relative">
          <div className="absolute inset-0 bg-gradient-to-b from-gray-50 to-white"></div>
          <div className="container mx-auto px-4 relative">
            <h2 className="text-4xl md:text-5xl font-bold text-center text-gray-900 mb-16">
              Our Services
            </h2>
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
              {features.map((feature, index) => (
                <div
                  key={feature.title}
                  className="group p-8 bg-white rounded-2xl shadow-lg hover:shadow-2xl transform transition-all duration-300 hover:-translate-y-2 relative overflow-hidden"
                >
                  <div className="absolute inset-0 bg-gradient-to-br from-indigo-50 to-purple-50 opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                  <div className="relative z-10">
                    <div className="w-16 h-16 mb-6 rounded-xl bg-gradient-to-br from-indigo-500 to-purple-500 flex items-center justify-center transform transition-transform duration-300 group-hover:scale-110 group-hover:rotate-3">
                      <feature.icon className="w-8 h-8 text-white" />
                    </div>
                    <h3 className="text-2xl font-bold text-gray-900 mb-4">
                      {feature.title}
                    </h3>
                    <p className="text-gray-600">
                      {feature.description}
                    </p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </section>

        {/* Stats Section */}
        <section className="py-20 bg-gradient-to-br from-indigo-900 via-purple-900 to-cyan-900 relative">
          <div className="absolute inset-0 bg-[radial-gradient(circle_at_50%_50%,_rgba(255,255,255,0.1)_0%,_rgba(0,0,0,0)_50%)]"></div>
          <div className="container mx-auto px-4 relative">
            <div className="grid grid-cols-2 md:grid-cols-4 gap-8 text-white">
              {[
                { number: '1M+', label: 'Parcels Delivered' },
                { number: '98%', label: 'Customer Satisfaction' },
                { number: '50+', label: 'Countries Covered' },
                { number: '24/7', label: 'Customer Support' }
              ].map((stat) => (
                <div
                  key={stat.label}
                  className="text-center transform transition-all duration-300 hover:scale-105"
                >
                  <div className="text-4xl md:text-5xl font-bold mb-2">
                    {stat.number}
                  </div>
                  <div className="text-lg opacity-90">
                    {stat.label}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </section>

        {/* About Section */}
        <section id="about" className="py-24 bg-white">
          <div className="container mx-auto px-4">
            <div className="max-w-3xl mx-auto text-center">
              <h2 className="text-4xl md:text-5xl font-bold text-gray-900 mb-8">
                About PostPlus
              </h2>
              <p className="text-xl text-gray-600 mb-12">
                We're revolutionizing the delivery industry with cutting-edge technology 
                and exceptional service. Our mission is to provide fast, reliable, and 
                innovative logistics solutions worldwide.
              </p>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                {/* Mission and Features */}
                <div className="p-8 bg-gradient-to-br from-indigo-50 to-indigo-100 rounded-lg shadow">
                  <h3 className="text-2xl font-semibold text-gray-800 mb-4">Our Mission</h3>
                  <p className="text-gray-600">
                    At PostPlus, our mission is to deliver excellence in logistics by leveraging advanced technology to ensure timely and efficient delivery of goods across the globe.
                  </p>
                  <ul className="mt-4 space-y-2">
                    <li className="flex items-center">
                      <CheckCircle className="w-5 h-5 text-indigo-600 mr-2" />
                      <span>Reliable Delivery</span>
                    </li>
                    <li className="flex items-center">
                      <CheckCircle className="w-5 h-5 text-indigo-600 mr-2" />
                      <span>Innovative Solutions</span>
                    </li>
                    <li className="flex items-center">
                      <CheckCircle className="w-5 h-5 text-indigo-600 mr-2" />
                      <span>Customer Satisfaction</span>
                    </li>
                  </ul>
                </div>
                
                {/* Illustrative Image */}
                <div className="p-8 bg-gradient-to-br from-indigo-50 to-indigo-100 rounded-lg shadow flex items-center justify-center">
                  <img 
                    src="/path-to-your-image.jpg" 
                    alt="About PostPlus" 
                    className="rounded-lg shadow-lg w-full h-auto object-cover"
                  />
                </div>
              </div>
            </div>
          </div>
        </section>

        {/* Call to Action */}
        <section className="py-20 bg-gradient-to-br from-indigo-900 via-purple-900 to-cyan-900 relative">
          <div className="absolute inset-0 bg-[radial-gradient(circle_at_50%_50%,_rgba(255,255,255,0.1)_0%,_rgba(0,0,0,0)_50%)]"></div>
          <div className="container mx-auto px-4 text-center relative">
            <h2 className="text-3xl md:text-4xl font-bold text-white mb-8">
              Ready to Transform Your Delivery Experience?
            </h2>
            <button className="px-8 py-4 bg-white text-indigo-900 rounded-full font-bold hover:bg-cyan-400 hover:text-white transition-all duration-300 transform hover:scale-105 shadow-lg">
              Start Shipping Now
            </button>
          </div>
        </section>
      </main>

      {/* Footer */}
      <footer className="bg-gray-900 text-white py-12">
        <div className="container mx-auto px-4">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-8 mb-8">
            <div>
              <div className="flex items-center space-x-2 mb-6">
                <div className="w-10 h-10 bg-gradient-to-br from-cyan-400 to-purple-600 rounded-lg flex items-center justify-center">
                  <span className="text-white font-bold text-xl">P+</span>
                </div>
                <span className="text-2xl font-bold">PostPlus</span>
              </div>
              <p className="text-gray-400">
                Revolutionizing delivery with cutting-edge technology and exceptional service.
              </p>
            </div>
            <div>
              <h4 className="text-lg font-bold mb-4">Quick Links</h4>
              <div className="space-y-2">
                {['Home', 'Services', 'About'].map((link) => (
                  <a
                    key={link}
                    href={`#${link.toLowerCase()}`}
                    className="block text-gray-400 hover:text-white transition-colors duration-300"
                  >
                    {link}
                  </a>
                ))}
              </div>
            </div>
            <div>
              <h4 className="text-lg font-bold mb-4">Services</h4>
              <div className="space-y-2">
                {['Express Delivery', 'Global Shipping', 'Tracking'].map((service) => (
                  <a
                    key={service}
                    href="#"
                    className="block text-gray-400 hover:text-white transition-colors duration-300"
                  >
                    {service}
                  </a>
                ))}
              </div>
            </div>
            <div>
              <h4 className="text-lg font-bold mb-4">Contact</h4>
              <div className="space-y-2">
                <p className="text-gray-400">Email: info@postplus.com</p>
                <p className="text-gray-400">Phone: +1 (555) 123-4567</p>
              </div>
            </div>
          </div>
          <div className="border-t border-gray-800 pt-8">
            <div className="flex flex-col md:flex-row justify-between items-center">
              <p className="text-gray-400 mb-4 md:mb-0">
                 2024 PostPlus. All rights reserved.
              </p>
              <div className="flex space-x-6">
                {['Privacy Policy', 'Terms of Service', 'Support'].map((link) => (
                  <a
                    key={link}
                    href="#"
                    className="text-gray-400 hover:text-white transition-colors duration-300"
                  >
                    {link}
                  </a>
                ))}
              </div>
            </div>
          </div>
        </div>
      </footer>

      {/* Scroll to Top Button */}
      <button
        onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
        className={`fixed bottom-8 right-8 p-4 bg-gradient-to-r from-indigo-600 to-purple-600 text-white rounded-full shadow-lg transition-all duration-300 hover:scale-110 ${
          isScrolled ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-16'
        }`}
      >
        <ArrowUp className="w-6 h-6" />
      </button>
    </div>
  );
};

export default HomePage;
