import React, { useEffect, useState, useRef } from 'react';
import maplibregl from 'maplibre-gl';
import 'maplibre-gl/dist/maplibre-gl.css';
import { UserContext } from './UserContext';
import { 
  Map as MapIcon, 
  Truck, 
  FileText, 
  Calendar, 
  UserPlus, 
  Bell, 
  Menu, 
  LogOut 
} from 'lucide-react';
import { Layout, Sidebar, MainContent } from '../components/ui/Layout';

const WarehouseDashboard = () => {
  const [activeTab, setActiveTab] = useState('incoming'); // 'incoming' or 'outgoing'
  const [warehouseId, setWarehouseId] = useState(''); // Warehouse ID
  const [trucks, setTrucks] = useState([]);
  const [error, setError] = useState(null);
  const [map, setMap] = useState(null);
  const [selectedTruck, setSelectedTruck] = useState(null);
  const [currentView, setCurrentView] = useState('fleet'); // Navigation tabs
  const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);
  const mapContainerRef = useRef(null);
  const { warehouseAccess } = useContext(UserContext); // Access warehouseAccess from context

  const menuItems = [
    { id: 'fleet', icon: <Truck className="w-5 h-5" />, label: 'Fleet' },
    { id: 'reports', icon: <FileText className="w-5 h-5" />, label: 'Reports' },
    { id: 'schedule', icon: <Calendar className="w-5 h-5" />, label: 'Schedule' },
    { id: 'add-user', icon: <UserPlus className="w-5 h-5" />, label: 'Add User' },
    { id: 'notifications', icon: <Bell className="w-5 h-5" />, label: 'Notifications' },
  ];

  useEffect(() => {
    if (!warehouseId) return;

    const endpoint =
      activeTab === 'incoming'
        ? `/api/getIncomingLiveTrucks?warehouseId=${warehouseId}`
        : `/api/getOutgoingTrucks?warehouseId=${warehouseId}`;

    fetch(endpoint)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Error: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setTrucks(data.incomingTrucks || data.outgoing_trucks || []);
      })
      .catch((err) => {
        setError(err.message);
      });
  }, [activeTab, warehouseId]);


  useEffect(() => {
    // Fetch trucks for all accessible warehouses
    if (warehouseAccess) {
      const warehouseIds = warehouseAccess.split(',').map((id) => id.trim()); // Split and trim IDs
      fetchAllTrucks(warehouseIds);
    }
  }, [warehouseAccess]);

  const fetchAllTrucks = async (warehouseIds) => {
    const allTrucks = [];
    const addedTruckIds = new Set(); // Track unique truck IDs

    for (const warehouseId of warehouseIds) {
      try {
        const response = await fetch(`/api/getScheduledTrucks?warehouseId=${warehouseId}`);
        const data = await response.json();

        if (data) {
          data.forEach((truck) => {
            if (!addedTruckIds.has(truck.truck_id)) {
              addedTruckIds.add(truck.truck_id);
              allTrucks.push(truck);
            }
          });
        }
      } catch (err) {
        console.error(`Error fetching trucks for warehouse ${warehouseId}:`, err);
      }
    }

    setTrucks(allTrucks);
  };

  useEffect(() => {
    if (!mapContainerRef.current) return;

    const mapInstance = new maplibregl.Map({
      container: mapContainerRef.current,
      style: 'https://demotiles.maplibre.org/style.json',
      center: [78.9629, 20.5937],
      zoom: 4,
    });

    setMap(mapInstance);
    return () => mapInstance.remove();
  }, []);

  const renderContent = () => {
    switch (currentView) {
      case 'fleet':
        return (
          <div>
            <h1>Fleet Management</h1>
            <div style={{ marginBottom: '20px' }}>
              <label>
                <strong>Warehouse ID:</strong>{' '}
                <input
                  type="text"
                  value={warehouseId}
                  onChange={(e) => setWarehouseId(e.target.value)}
                  placeholder="Enter Warehouse ID"
                  style={{ padding: '5px', marginRight: '10px' }}
                />
              </label>
              <button
                onClick={() => {
                  if (!warehouseId) {
                    setError('Please enter a valid warehouse ID.');
                    return;
                  }
                  setError(null);
                }}
                style={{
                  padding: '5px 10px',
                  backgroundColor: '#007bff',
                  color: '#fff',
                  border: 'none',
                  cursor: 'pointer',
                }}
              >
                Fetch Trucks
              </button>
            </div>
            <div>
              <button
                style={{
                  padding: '10px 20px',
                  marginRight: '10px',
                  backgroundColor: activeTab === 'incoming' ? '#007bff' : '#ccc',
                  color: '#fff',
                  border: 'none',
                  cursor: 'pointer',
                }}
                onClick={() => setActiveTab('incoming')}
              >
                Incoming Trucks
              </button>
              <button
                style={{
                  padding: '10px 20px',
                  backgroundColor: activeTab === 'outgoing' ? '#007bff' : '#ccc',
                  color: '#fff',
                  border: 'none',
                  cursor: 'pointer',
                }}
                onClick={() => setActiveTab('outgoing')}
              >
                Outgoing Trucks
              </button>
            </div>
            {error ? (
              <p style={{ color: 'red' }}>Error: {error}</p>
            ) : (
              <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                  <tr>
                    <th>Truck ID</th>
                    <th>Name</th>
                    <th>Current Location</th>
                    <th>Destination</th>
                    <th>Status</th>
                  </tr>
                </thead>
                <tbody>
                  {trucks.length > 0 ? (
                    trucks.map((truck) => (
                      <tr key={truck.truck_id} style={{ cursor: 'pointer' }}>
                        <td>{truck.truck_id}</td>
                        <td>{truck.name}</td>
                        <td>{truck.current_location}</td>
                        <td>{truck.current_destination}</td>
                        <td>{truck.truck_status}</td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="5" style={{ textAlign: 'center' }}>
                        No {activeTab === 'incoming' ? 'incoming' : 'outgoing'} trucks found.
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            )}
          </div>
        );
      case 'reports':
        return <div>Reports Section</div>;
      case 'schedule':
        return <div>Schedule Section</div>;
      case 'add-user':
        return <div>Add User Section</div>;
      case 'notifications':
        return <div>Notifications Section</div>;
      default:
        return <div>Fleet Management</div>;
    }
  };

  const handleLogout = () => {
    console.log('Logging out...');
  };

  return (
    <Layout>
      <Sidebar
        collapsed={isSidebarCollapsed}
        className={`h-screen bg-gray-900 text-white transition-all duration-300 ${
          isSidebarCollapsed ? 'w-20' : 'w-64'
        }`}
      >
        <div className="flex flex-col h-full">
          <div className="p-4 flex items-center justify-between border-b border-gray-800">
            {!isSidebarCollapsed && <h1 className="text-xl font-bold">Dashboard</h1>}
            <button
              onClick={() => setIsSidebarCollapsed(!isSidebarCollapsed)}
              className="p-2 rounded-lg hover:bg-gray-800 transition-colors"
            >
              <Menu className="w-5 h-5" />
            </button>
          </div>

          <nav className="flex-1 py-4">
            <ul className="space-y-2">
              {menuItems.map((item) => (
                <li key={item.id}>
                  <button
                    onClick={() => setCurrentView(item.id)}
                    className={`w-full p-3 flex items-center space-x-3 rounded-lg transition-colors ${
                      currentView === item.id
                        ? 'bg-blue-600 text-white'
                        : 'text-gray-300 hover:bg-gray-800'
                    }`}
                  >
                    {item.icon}
                    {!isSidebarCollapsed && <span>{item.label}</span>}
                  </button>
                </li>
              ))}
            </ul>
          </nav>

          <div className="p-4 border-t border-gray-800">
            <button
              onClick={handleLogout}
              className="w-full p-3 flex items-center space-x-3 text-gray-300 hover:bg-gray-800 rounded-lg transition-colors"
            >
              <LogOut className="w-5 h-5" />
              {!isSidebarCollapsed && <span>Logout</span>}
            </button>
          </div>
        </div>
      </Sidebar>

      <MainContent className="flex-1 bg-gray-100">
        <div className="h-full overflow-auto">
          {renderContent()}
        </div>
        <div
          ref={mapContainerRef}
          id="map"
          style={{
            flex: 2,
            height: '100vh',
            position: 'relative',
          }}
        ></div>
      </MainContent>
    </Layout>
  );
};

export default WarehouseDashboard;
