import React, { useState, useEffect } from 'react';

const Reports = () => {
  const [trucks, setTrucks] = useState([]);
  const [reportData, setReportData] = useState([]);

  // Fetch all trucks and their details on component mount
  useEffect(() => {
    fetchTrucks();
  }, []);

  const fetchTrucks = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/devices', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ key: 'value' }),
      });

      if (!response.ok) throw new Error('Failed to fetch trucks');

      const data = await response.json();
      setTrucks(data || []);
      fetchTruckDetails(data || []);
    } catch (error) {
      console.error('Error fetching trucks:', error);
    }
  };

  const fetchTruckDetails = async (trucks) => {
    try {
      const details = await Promise.all(
        trucks.map(async (truck) => {
          const capacity = await getTruckCapacity(truck.uniqueId);
          const status = await getTruckStatus(truck.uniqueId);
          const location = await getTruckLocation(truck.uniqueId);

          // Extract and round the filled capacity
          const filledCapacity =
            capacity.incoming_trucks && capacity.incoming_trucks.length > 0
              ? Math.round(capacity.incoming_trucks[0].filled_capacity * 100) / 100 // Round to 2 decimals
              : 'N/A';

          const currentStatus = status.status || 'N/A';
          const atWarehouse = status.at_warehouse ? 'Yes' : 'No';
          const onRoute = status.on_route ? 'Yes' : 'No';

          const currentLocationName = location.current_location_name || 'N/A';
          const currentDestinationName = location.current_destination_name || 'N/A';

          return {
            truckId: truck.uniqueId,
            name: truck.name || `Truck ${truck.uniqueId}`,
            capacityUtilization: filledCapacity,
            currentStatus,
            atWarehouse,
            onRoute,
            currentLocation: currentLocationName,
            currentDestination: currentDestinationName,
            lastUpdated: status.lastUpdated || 'N/A',
          };
        })
      );

      setReportData(details);
    } catch (error) {
      console.error('Error fetching truck details:', error);
    }
  };

  const getTruckCapacity = async (uniqueId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/getTruckCapacity?truckId=${uniqueId}`);
      if (!response.ok) throw new Error('Failed to fetch truck capacity');
      return await response.json();
    } catch (error) {
      console.error(`Error fetching capacity for truck ${uniqueId}:`, error);
      return {};
    }
  };

  const getTruckStatus = async (uniqueId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/getTruckStatus?uniqueId=${uniqueId}`);
      if (!response.ok) throw new Error('Failed to fetch truck status');
      return await response.json();
    } catch (error) {
      console.error(`Error fetching status for truck ${uniqueId}:`, error);
      return {};
    }
  };

  const getTruckLocation = async (uniqueId) => {
    try {
      const response = await fetch('http://localhost:8000/api/getTruckLocation', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ truck_id: uniqueId }),
      });

      if (!response.ok) throw new Error('Failed to fetch truck location');
      return await response.json();
    } catch (error) {
      console.error(`Error fetching location for truck ${uniqueId}:`, error);
      return {};
    }
  };

  return (
    <div className="p-6 bg-white rounded-lg shadow">
      <h2 className="text-2xl font-bold mb-4">Truck Status Reports</h2>

      {/* Report Table */}
      {reportData.length > 0 ? (
        <table className="w-full border-collapse border border-gray-300">
          <thead>
            <tr>
              <th className="border border-gray-300 px-4 py-2">Truck ID</th>
              <th className="border border-gray-300 px-4 py-2">Truck Name</th>
              <th className="border border-gray-300 px-4 py-2">Capacity Utilization</th>
              <th className="border border-gray-300 px-4 py-2">Current Status</th>
              <th className="border border-gray-300 px-4 py-2">At Warehouse</th>
              <th className="border border-gray-300 px-4 py-2">On Route</th>
              <th className="border border-gray-300 px-4 py-2">Current Location</th>
              <th className="border border-gray-300 px-4 py-2">Current Destination</th>
              <th className="border border-gray-300 px-4 py-2">Last Updated</th>
            </tr>
          </thead>
          <tbody>
            {reportData.map((report, index) => (
              <tr key={index}>
                <td className="border border-gray-300 px-4 py-2">{report.truckId}</td>
                <td className="border border-gray-300 px-4 py-2">{report.name}</td>
                <td className="border border-gray-300 px-4 py-2">{report.capacityUtilization}</td>
                <td className="border border-gray-300 px-4 py-2">{report.currentStatus}</td>
                <td className="border border-gray-300 px-4 py-2">{report.atWarehouse}</td>
                <td className="border border-gray-300 px-4 py-2">{report.onRoute}</td>
                <td className="border border-gray-300 px-4 py-2">{report.currentLocation}</td>
                <td className="border border-gray-300 px-4 py-2">{report.currentDestination}</td>
                <td className="border border-gray-300 px-4 py-2">{report.lastUpdated}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p className="text-gray-500">Loading truck details...</p>
      )}
    </div>
  );
};

export default Reports;
