import React, { useEffect, useRef, useState } from 'react';
import maplibregl from 'maplibre-gl';

const AddWarehouse = () => {
  const mapContainer = useRef(null);
  const map = useRef(null);
  const [currentPopup, setCurrentPopup] = useState(null);
  const [warehouses, setWarehouses] = useState([]);

  useEffect(() => {
    if (mapContainer.current && !map.current) {
      map.current = new maplibregl.Map({
        container: mapContainer.current,
        style: 'https://basemaps.cartocdn.com/gl/voyager-gl-style/style.json',
        center: [78.5, 10.5],
        zoom: 5,
      });

      map.current.on('click', (e) => {
        const { lng, lat } = e.lngLat;
        showCreateWarehousePopup(lng, lat);
      });

      fetchGeofences();

      return () => {
        if (map.current) {
          map.current.remove();
          map.current = null;
        }
      };
    }
  }, []);

  const fetchGeofences = async () => {
    try {
      const response = await fetch('http://localhost:3000/api/geofences', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        console.error('Failed to fetch geofences:', response.status, response.statusText);
        return;
      }

      const data = await response.json();
      data.forEach((geofence) => {
        if (geofence.area.startsWith('POLYGON')) {
          displayGeofenceOnMap(geofence);
        }
      });
    } catch (error) {
      console.error('Error fetching geofences:', error);
    }
  };

  const showCreateWarehousePopup = (lng, lat) => {
    if (currentPopup) currentPopup.remove();

    const popupContent = document.createElement('div');
    popupContent.className = 'p-4';
    popupContent.innerHTML = `
      <div class="space-y-4">
        <p class="font-medium">Create a warehouse at:</p>
        <p class="text-gray-600"><strong>${lng.toFixed(4)}, ${lat.toFixed(4)}</strong></p>
        
        <div class="space-y-2">
          <label class="block">
            <span class="text-gray-700">Warehouse Name:</span>
            <input 
              type="text" 
              id="warehouseName"
              class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              placeholder="Enter warehouse name"
              required
            />
          </label>
        </div>

        <div class="space-y-2">
          <label class="block">
            <span class="text-gray-700">Load Time (minutes):</span>
            <input 
              type="number" 
              id="loadTime"
              class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              min="0"
              required
            />
          </label>
        </div>

        <div class="space-y-2">
          <label class="block">
            <span class="text-gray-700">Unload Time (minutes):</span>
            <input 
              type="number" 
              id="unloadTime"
              class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              min="0"
              required
            />
          </label>
        </div>

        <button 
          id="createWarehouseBtn" 
          class="w-full bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition-colors"
        >
          Create Warehouse
        </button>
      </div>
    `;

    setTimeout(() => {
      const button = document.getElementById('createWarehouseBtn');
      if (button) {
        button.addEventListener('click', () => {
          const nameInput = document.getElementById('warehouseName');
          const loadTimeInput = document.getElementById('loadTime');
          const unloadTimeInput = document.getElementById('unloadTime');

          if (!nameInput.value || !loadTimeInput.value || !unloadTimeInput.value) {
            alert('Please fill in all fields');
            return;
          }

          handleAddWarehouse(lng, lat, {
            name: nameInput.value,
            loadTime: parseInt(loadTimeInput.value),
            unloadTime: parseInt(unloadTimeInput.value)
          });
        });
      }
    }, 0);

    const popup = new maplibregl.Popup({ offset: 25 })
      .setLngLat([lng, lat])
      .setDOMContent(popupContent)
      .addTo(map.current);

    setCurrentPopup(popup);
  };

  const handleAddWarehouse = async (lng, lat, warehouseDetails) => {
    const newWarehouse = {
      id: warehouses.length + 1,
      lng,
      lat,
      ...warehouseDetails
    };

    setWarehouses([...warehouses, newWarehouse]);
    addWarehouseMarker(lng, lat, newWarehouse);

    if (currentPopup) {
      currentPopup.remove();
      setCurrentPopup(null);
    }

    await createGeofence(newWarehouse);
  };

  const createGeofence = async (warehouse) => {
    const delta = 0.0003;

    const rectangleCoordinates = [
      [warehouse.lat + delta, warehouse.lng - delta],
      [warehouse.lat + delta, warehouse.lng + delta],
      [warehouse.lat - delta, warehouse.lng + delta],
      [warehouse.lat - delta, warehouse.lng - delta],
      [warehouse.lat + delta, warehouse.lng - delta],
    ];

    const geofenceData = {
      name: warehouse.name,
      description: `Load Time: ${warehouse.loadTime}min, Unload Time: ${warehouse.unloadTime}min`,
      area: `POLYGON((${rectangleCoordinates.map((coord) => coord.join(' ')).join(', ')}))`,
      attributes: {
        loadTime: warehouse.loadTime,
        unloadTime: warehouse.unloadTime
      }
    };

    try {
      const response = await fetch('http://localhost:3000/api/geofences', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(geofenceData),
      });

      if (!response.ok) {
        throw new Error('Failed to create geofence');
      }

      const data = await response.json();
      displayGeofenceOnMap(data);
      alert(`Warehouse "${warehouse.name}" created successfully!`);
    } catch (error) {
      console.error('Error creating geofence:', error);
      alert('Failed to create warehouse. Please try again.');
    }
  };

  const addWarehouseMarker = (lng, lat, warehouse) => {
    new maplibregl.Marker({ color: '#007bff' })
      .setLngLat([lng, lat])
      .setPopup(
        new maplibregl.Popup({ offset: 25 }).setHTML(`
          <div class="p-3">
            <h3 class="font-bold mb-2">${warehouse.name}</h3>
            <p><strong>Location:</strong> ${lng.toFixed(4)}, ${lat.toFixed(4)}</p>
            <p><strong>Load Time:</strong> ${warehouse.loadTime} minutes</p>
            <p><strong>Unload Time:</strong> ${warehouse.unloadTime} minutes</p>
          </div>
        `)
      )
      .addTo(map.current);
  };

  const displayGeofenceOnMap = (geofence) => {
    const match = /POLYGON\(\(\s*([^)]+)\s*\)\)/.exec(geofence.area);
    if (!match) {
      console.warn('Could not parse geofence area:', geofence.area);
      return;
    }

    const coordinates = match[1]
      .split(', ')
      .map((coord) => coord.split(' ').map(Number))
      .map((coord) => [coord[1], coord[0]]);

    const sourceId = `geofence-${geofence.id}`;

    if (!map.current.getSource(sourceId)) {
      map.current.addSource(sourceId, {
        type: 'geojson',
        data: {
          type: 'FeatureCollection',
          features: [
            {
              type: 'Feature',
              geometry: {
                type: 'Polygon',
                coordinates: [coordinates],
              },
            },
          ],
        },
      });

      map.current.addLayer({
        id: `geofence-layer-${geofence.id}`,
        type: 'fill',
        source: sourceId,
        paint: {
          'fill-color': '#ff0000',
          'fill-opacity': 0.3,
        },
      });

      map.current.addLayer({
        id: `geofence-outline-${geofence.id}`,
        type: 'line',
        source: sourceId,
        paint: {
          'line-color': '#ff0000',
          'line-width': 2,
        },
      });
      
      new maplibregl.Marker({ color: '#ff0000' })
        .setLngLat([coordinates[0][0], coordinates[0][1]])
        .setPopup(
          new maplibregl.Popup({ offset: 25 }).setHTML(`
            <div class="p-3">
              <h3 class="font-bold mb-2">${geofence.name}</h3>
              <p>${geofence.description}</p>
            </div>
          `)
        )
        .addTo(map.current);
    }
  };

  return (
    <div style={{ height: '100vh', position: 'relative' }}>
      <h1 className="absolute z-10 m-4 text-2xl font-bold">Add Warehouse</h1>
      <div
        ref={mapContainer}
        className="absolute inset-0"
      />
      <button
        className="absolute top-5 right-5 z-10 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors"
        onClick={() => {
          if (map.current) {
            map.current.easeTo({
              zoom: 5,
              center: [78.9629, 20.5937],
              duration: 1000,
            });
          }
        }}
      >
        Reset View
      </button>
    </div>
  );
};

export default AddWarehouse;
