import React, { useState, useEffect } from 'react';

const AddTruck = () => {
  const [formData, setFormData] = useState({
    name: '',
    uniqueId: '',
    max_volume: '',
    max_weight: '',
    mms_allocated_id: '',
    driver_id: '',
  });
  
  const [drivers, setDrivers] = useState([]);
  const [mmsList, setMmsList] = useState([]);
  const [message, setMessage] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    fetchDrivers();
    fetchMMS();
  }, []);

  const fetchDrivers = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/getAllDrivers', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ key: 'value' }),
      });
      
      if (!response.ok) throw new Error('Failed to fetch drivers');
      
      const data = await response.json();
      setDrivers(data.drivers || []);
    } catch (error) {
      console.error('Error fetching drivers:', error);
      setMessage({ type: 'error', text: 'Failed to load drivers' });
    }
  };

  const fetchMMS = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/getAllMMS', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ key: 'value' }),
      });
      
      if (!response.ok) throw new Error('Failed to fetch MMS');
      
      const data = await response.json();
      setMmsList(data.mms || []);
    } catch (error) {
      console.error('Error fetching MMS:', error);
      setMessage({ type: 'error', text: 'Failed to load MMS data' });
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    
    try {
      const response = await fetch('http://localhost:8000/api/devices/new', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name: formData.name,
          uniqueId: formData.uniqueId,
          max_volume: parseInt(formData.max_volume),
          max_weight: parseInt(formData.max_weight),
          mms_allocated_id: parseInt(formData.mms_allocated_id),
          driver_id: parseInt(formData.driver_id),
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to add truck');
      }

      const data = await response.json();
      setMessage({ type: 'success', text: `Truck ${data.name} added successfully!` });
      
      // Reset form
      setFormData({
        name: '',
        uniqueId: '',
        max_volume: '',
        max_weight: '',
        mms_allocated_id: '',
        driver_id: '',
      });
    } catch (error) {
      console.error('Error adding truck:', error);
      setMessage({ type: 'error', text: error.message || 'Failed to add truck. Please try again.' });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-6">Add New Truck</h2>
      
      {message && (
        <div className={`p-4 mb-6 rounded-md ${
          message.type === 'success' ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'
        }`}>
          {message.text}
        </div>
      )}
      
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-sm font-medium mb-1">
            Truck Name
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              required
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
              placeholder="Enter truck name"
            />
          </label>
        </div>

        <div>
          <label className="block text-sm font-medium mb-1">
            Unique ID
            <input
              type="text"
              name="uniqueId"
              value={formData.uniqueId}
              onChange={handleInputChange}
              required
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
              placeholder="Enter unique ID"
            />
          </label>
        </div>

        <div>
          <label className="block text-sm font-medium mb-1">
            Max Volume
            <input
              type="number"
              name="max_volume"
              value={formData.max_volume}
              onChange={handleInputChange}
              required
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
              placeholder="Enter max volume"
            />
          </label>
        </div>

        <div>
          <label className="block text-sm font-medium mb-1">
            Max Weight
            <input
              type="number"
              name="max_weight"
              value={formData.max_weight}
              onChange={handleInputChange}
              required
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
              placeholder="Enter max weight"
            />
          </label>
        </div>

        <div>
          <label className="block text-sm font-medium mb-1">
            MMS
            <select
              name="mms_allocated_id"
              value={formData.mms_allocated_id}
              onChange={handleInputChange}
              required
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
            >
              <option value="">Select MMS</option>
              {mmsList.map((mms) => (
                <option key={mms.id} value={mms.id}>
                  {mms.name}
                </option>
              ))}
            </select>
          </label>
        </div>

        <div>
          <label className="block text-sm font-medium mb-1">
            Driver
            <select
              name="driver_id"
              value={formData.driver_id}
              onChange={handleInputChange}
              required
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
            >
              <option value="">Select Driver</option>
              {drivers.map((driver) => (
                <option key={driver.id} value={driver.id}>
                  {driver.name}
                </option>
              ))}
            </select>
          </label>
        </div>

        <button
          type="submit"
          disabled={isLoading}
          className="w-full bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          {isLoading ? 'Adding Truck...' : 'Add Truck'}
        </button>
      </form>
    </div>
  );
};

export default AddTruck;
